import React, { useState } from "react";

import classes from "../Pages/ContactInfo.module.css";

import ContactItem from "../Pages/ContactItem";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPencil, faTimes, faCheck } from "@fortawesome/free-solid-svg-icons";

const ContactInfo = (props) => {
  const [formIsShown, setFormIsShown] = useState(false);
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(true);
  const [enteredSurName, setEnteredSurName] = useState("");

  const showEl = () => {
    setFormIsShown(true);
  };

  const inputName = (event) => {
    setEnteredName(event.target.value);
  };

  const inputSurName = (event) => {
    setEnteredSurName(event.target.value);
  };

  const preventDefaultHandler = (event) => {
    event.preventDefault();

    if (enteredName.trim() === "") {
      setEnteredNameIsValid(false);
      return;
    }

    setEnteredNameIsValid(true);

    console.log(enteredName);
    console.log(enteredSurName);
  };

  return (
    <section className={classes["contact-info"]}>
      <main className={classes["contact-better"]}>
        <div className={classes.buttons}>
          {!formIsShown ? (
            <button className={classes.pencil}>
              <FontAwesomeIcon icon={faPencil} />
            </button>
          ) : (
            <button className={classes.check}>
              <FontAwesomeIcon icon={faCheck} onClick={preventDefaultHandler} />
            </button>
          )}
          <button className={classes.close}>
            <FontAwesomeIcon icon={faTimes} />
          </button>
          <button className={classes["add-new-user-btn"]} onClick={showEl}>
            Add New Contact
          </button>
        </div>
        {!formIsShown ? (
          <ContactItem
            key={props.contact.id}
            name={props.contact.name}
            mobile={props.contact.mobile}
            home={props.contact.home}
            company={props.contact.company}
            work={props.contact.work}
            notes={props.contact.notes}
          />
        ) : (
          <form
            className={classes["add-contact-wrapper"]}
            onSubmit={preventDefaultHandler}
          >
            <div className={classes["image-wrapper"]}>
              <img
                src="https://ziadfathy.github.io/ZfTemplate/images/01.jpg"
                alt="#"
                width="120px"
                height="120px"
              ></img>
              <h1>No Name</h1>
            </div>
            <div className={classes["inputs-wrapper"]}>
              <div className={classes["contact-wrapper"]}>
                <p>First Name:</p>
                <input
                  className={
                    enteredNameIsValid
                      ? classes["contacts-inputs"]
                      : classes["invalid-input"]
                  }
                  placeholder="First Name"
                  type="text"
                  value={enteredName}
                  onChange={inputName}
                ></input>
                {!enteredNameIsValid && (
                  <h5 className={classes.validation}>
                    Entered name must not be empty!
                  </h5>
                )}
              </div>
              <div className={classes["contact-wrapper"]}>
                <p>Last Name:</p>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="Last Name"
                  type="text"
                  value={enteredSurName}
                  onChange={inputSurName}
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <p>Mobile:</p>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="Mobile"
                  type="text"
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <p>Home:</p>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="Home"
                  type="text"
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <p>Company:</p>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="Company"
                  type="text"
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <p>Work:</p>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="Work"
                  type="text"
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <p>Notes:</p>
                <textarea
                  className={classes.notes}
                  placeholder="Notes"
                  type="textarea"
                  rows="5"
                ></textarea>
              </div>
            </div>
          </form>
        )}
      </main>
    </section>
  );
};

export default ContactInfo;
