import React, { useState } from "react";

import classes from "../Pages/ContactInfo.module.css";

import ContactItem from "../Pages/ContactItem";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPencil, faTimes, faCheck } from "@fortawesome/free-solid-svg-icons";
import { useFormik } from "formik";

const ContactInfo = (props) => {
  const [formIsShown, setFormIsShown] = useState(false);
  // const [enteredName, setEnteredName] = useState("");
  // const [enteredNameIsValid, setEnteredNameIsValid] = useState(true);
  // const [enteredSurName, setEnteredSurName] = useState("");

  const showEl = () => {
    setFormIsShown(true);
  };

  // const inputName = (event) => {
  //   setEnteredName(event.target.value);
  // };

  // const inputSurName = (event) => {
  //   setEnteredSurName(event.target.value);
  // };

  // const preventDefaultHandler = (event) => {
  //   event.preventDefault();

  //   if (enteredName.trim() === "") {
  //     setEnteredNameIsValid(false);
  //     return;
  //   }

  //   setEnteredNameIsValid(true);

  //   console.log(enteredName);
  //   console.log(enteredSurName);
  // };

  const formik = useFormik({
    initialValues: {
      firstName: "",
      lastName: "",
      mobile: "",
      home: "",
      company: "",
      work: "",
      notes: "",
    },
    onSubmit: (values) => {
      alert(JSON.stringify(values, null, 2));
    },
  });

  const validate = (values) => {
    const errors = {};

    if (!values.firstName) {
      errors.firstName = "Required";
    } else if (values.firstName.length < 3) {
      errors.firstName = "Must be 3 characters or more";
    }

    if (!values.lastName) {
      errors.lastName = "Required";
    } else if (values.lastName.length < 4) {
      errors.lastName = "Must be 4 characters or more";
    }

    if (!values.mobile) {
      errors.mobile = "Required";
    } else if (values.mobile.length < 11) {
      errors.mobile = "Must be 11 characters or more";
    }

    if (!values.home) {
      errors.home = "Required";
    } else if (values.home.length < 11) {
      errors.home = "Must be 11 characters or more";
    }

    if (!values.company) {
      errors.company = "Required";
    } else if (values.company.length < 4) {
      errors.company = "Must be 4 characters or more";
    }

    if (!values.work) {
      errors.work = "Required";
    } else if (values.work.length < 11) {
      errors.work = "Must be 11 characters or more";
    }

    if (!values.notes) {
      errors.notes = "Required";
    } else if (values.notes.length < 5) {
      errors.notes = "Must be 5 characters or more";
    }
  };

  return (
    <section className={classes["contact-info"]}>
      <main className={classes["contact-better"]}>
        {!formIsShown ? (
          <div className={classes.buttons}>
            {!formIsShown ? (
              <button className={classes.pencil}>
                <FontAwesomeIcon icon={faPencil} />
              </button>
            ) : (
              <button type="submit" className={classes.check}>
                <FontAwesomeIcon icon={faCheck} />
              </button>
            )}
            <button className={classes.close}>
              <FontAwesomeIcon icon={faTimes} />
            </button>
            <button className={classes["add-new-user-btn"]} onClick={showEl}>
              Add New Contact
            </button>
          </div>
        ) : (
          <div className={classes.buttons}></div>
        )}
        {!formIsShown ? (
          <ContactItem
            key={props.contact.id}
            name={props.contact.name}
            mobile={props.contact.mobile}
            home={props.contact.home}
            company={props.contact.company}
            work={props.contact.work}
            notes={props.contact.notes}
          />
        ) : (
          <form
            className={classes["add-contact-wrapper"]}
            onSubmit={formik.handleSubmit}
          >
            <div className={classes["image-wrapper"]}>
              <img
                src="https://ziadfathy.github.io/ZfTemplate/images/01.jpg"
                alt="#"
                width="120px"
                height="120px"
              ></img>
              <h1>No Name</h1>
            </div>
            <div className={classes["inputs-wrapper"]}>
              <div className={classes["contact-wrapper"]}>
                <label htmlFor="first-name">First Name:</label>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="First Name"
                  type="text"
                  name="firstName"
                  id="firstName"
                  onChange={formik.handleChange}
                  value={formik.values.firstName}
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <label htmlFor="last-name">Last Name:</label>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="Last Name"
                  type="text"
                  name="lastName"
                  id="lastName"
                  onChange={formik.handleChange}
                  value={formik.values.lastName}
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <label htmlFor="mobile">Mobile:</label>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="Mobile"
                  type="text"
                  name="mobile"
                  id="mobile"
                  onChange={formik.handleChange}
                  value={formik.values.mobile}
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <label htmlFor="home">Home:</label>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="Home"
                  type="text"
                  name="home"
                  id="home"
                  onChange={formik.handleChange}
                  value={formik.values.home}
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <label htmlFor="company">Company:</label>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="Company"
                  type="text"
                  name="company"
                  id="company"
                  onChange={formik.handleChange}
                  value={formik.values.company}
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <label htmlFor="work">Work:</label>
                <input
                  className={classes["contacts-inputs"]}
                  placeholder="Work"
                  type="text"
                  name="work"
                  id="work"
                  onChange={formik.handleChange}
                  value={formik.values.work}
                ></input>
              </div>
              <div className={classes["contact-wrapper"]}>
                <label htmlFor="notes">Notes:</label>
                <textarea
                  className={classes.notes}
                  placeholder="Notes"
                  type="textarea"
                  rows="5"
                  name="notes"
                  id="notes"
                  onChange={formik.handleChange}
                  value={formik.values.notes}
                ></textarea>
              </div>
              <button type="submit" className={classes.submitBtn}>
                Accept
              </button>
            </div>
          </form>
        )}
      </main>
    </section>
  );
};

export default ContactInfo;
